mapper.select:
select c.num, c.unum, c.mnum, c.snum, m.name as menuname, m.price as mprice, c.mcount, c.mtotalprice
from menu m, cart c
where c.mnum = m.num and c.unum = #{num}

mapper.update:
update cart
set mcount=mcount+#{mcount}, mtotalprice=mtotalprice+#{mtotalprice}
where num=#{num}

update cart
set mcount=mcount+1, mtotalprice=mtotalprice+(select price from menu where num=#{mnum})
where num=#{num}

update cart
set mcount=mcount-1, mtotalprice=mtotalprice-(select price from menu where num=#{mnum})
where num=#{num}
